mapper.select:
select deliverable.*, project.title as projectTitle, project.status as projectStatus, submission.title as submissionTitle, user.name as freelancerName from deliverable left join project on deliverable.project_id = project.id left join submission on deliverable.submission_id = submission.id left join freelancer on deliverable.freelancer_id = freelancer.id left join user on freelancer.user_id = user.id
and deliverable.submission_id = #{submissionId} and deliverable.project_id = #{projectId} and deliverable.freelancer_id = #{freelancerId} and deliverable.status = #{status}
order by deliverable.created_at desc

mapper.update:
update deliverable
title = #{title}, description = #{description}, status = #{status}, submit_count = #{submitCount}, submitted_at = #{submittedAt}, reviewed_at = #{reviewedAt}, review_comment = #{reviewComment},
where id = #{id}
