mapper.select:
select project_order.*, project.title as projectTitle, project.status as projectStatus, user.name as freelancerName, user.avatar as freelancerAvatar from project_order left join project on project_order.project_id = project.id left join freelancer on project_order.freelancer_id = freelancer.id left join user on freelancer.user_id = user.id
and project_order.project_id = #{projectId} and project_order.freelancer_id = #{freelancerId} and project_order.status = #{status}
order by project_order.accepted_at desc

mapper.update:
update project_order
status = #{status}, abandoned = #{abandoned}, abandoned_at = #{abandonedAt},
where id = #{id}
